import React, { useState } from "react";

function App() {
  const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: ""
  });

  function changeState(event){
    const {name, value} = event.target
    name == "fName" ? setContact({fName: value, lName:contact.lName}) : name === "lName" ? setContact({fName: contact.fName, lName: value}) : setContact({fName:contact.fName, lName: contact.lName, email: name})
  }

  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form>
        <input onChange={changeState} name="fName" placeholder="First Name" />
        <input onChange={changeState} name="lName" placeholder="Last Name" />
        <input onChange={changeState} name="email" placeholder="Email" />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
