import React from "react";


function App() {

  const [trial, letsGo] = React.useState(<button onMouseOver={over} onMouseOut={out}>Submit</button>)

  function over() {
    return letsGo(<button onMouseOver={over} onMouseOut={out} style={{ "backgroundColor": "black", "color": "white" }}>Submit</button>)
  }

  function out() {
    return letsGo(<button onMouseOver={over} onMouseOut={out}>Submit</button>)
  }

  return (
    <div className="container">
      <h1>Hello</h1>
      <input type="text" placeholder="What's your name?" />
      {trial}
    </div>
  );
}


export default App;
