import React from "react";

let mouseOver = false

function over(){
  mouseOver = true
  console.log("Over")
}

function out(){
  mouseOver=false
  console.log("Out")
}



function App() {
  return (
    <div className="container">
      <h1>Hello</h1>
      <input type="text" placeholder="What's your name?" />
      {mouseOver ? <button onMouseOver={over} onMouseOut={out} style={{"backgroundColor" : "black"}}>Submit</button> : <button onMouseOver={over} onMouseOut={out}>Submit</button> } 
    </div>
  );
}


export default App;
