import React from "react";


function App() {

  const [trial, letsGo] = React.useState(false)

  function mouseOver() {
    letsGo(true)
    return letsGo(<button onMouseOver={mouseOver} onMouseOut={mouseOut} style={{ "backgroundColor": "black", "color": "white" }}>Submit</button>)
  }

  function mouseOut() {
    return letsGo(<button onMouseOver={mouseOver} onMouseOut={mouseOut}>Submit</button>)
  }

  return (
    <div className="container">
      <h1>Hello</h1>
      <input type="text" placeholder="What's your name?" />
      <button onMouseOver={mouseOver} onMouseOut={mouseOut}>Submit</button>
    </div>
  );
}


export default App;
