import React from "react";

let mouseOver = false

function over(){
  mouseOver = true
}

function out(){
  mouseOver=false
}



function App() {
  return (
    <div className="container">
      <h1>Hello</h1>
      <input type="text" placeholder="What's your name?" />
      {mouseOver ? <button onMouseOver={over} style="background-color: black ;">Submit</button> : <button onMouseOut={out}>Submit</button> }
    </div>
  );
}

export default App;
