import React from "react";

function App() {

  let items = []

  const [todoItem, manageItem] = React.useState()

  function chnge(event) {
    const { value } = event.target

    manageItem(value)
  }

  function sbmit() {
    items.push(todoItem)
    document.getElementById("dlt").value = ""
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input id="dlt" onChange={chnge} type="text" />
        <button onClick={sbmit}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {items.map(item => <li>{item}</li>)}
        </ul>
      </div>
    </div>
  );
}

export default App;
