import React from "react";

function App() {

  const [todoItem, manageItem] = React.useState()

  let itms = []

  function chnge(event){
    manageItem(event.target.value)
  }

  function sbmit(){
    itms.push(todoItem)
    manageItem(itms)
  }

  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={chnge} type="text" />
        <button onClick={sbmit}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
        {todoItem.map(items =>{
          <li>{items}</li>
        })}
        </ul>
      </div>
    </div>
  );
}

export default App;
