import React from "react";

function App() {

  const [time, currentTime] = React.useState("TIME")

  function showTime(){
    time = new Date().toLocaleTimeString();
  }

  return (
    <div className="container">
      <h1>{time}</h1>
      <button>Get Time</button>
    </div>
  );
}

export default App;
