import React from "react";


function tru(input){
  return input
}

function App() {
  const [count, setCount] = React.useState(0)
  console.log(setCount)

  function increase(){
    setCount(count+1)
  }
  function decrease(){
    setCount(count-1)
  }

  return (
    <div className="container">
      <h1>{count}</h1>
      <button onClick={increase}>+</button>
      <button onClick={decrease}>-</button>
    </div>
  );

}

export default App;
